nodes.comment:
byte note = 0;
byte isNoteOn = 0;
byte isSustain = 0;
float newAmp = 0.0;
byte oscApitchMult = 64; // set at middle
byte oscBpitchMult = 64; // set at middle
byte oscCpitchMult = 64; // set at middle
byte oscDpitchMult = 64; // set at middle

/**
 * this function inits the notegen
 */
void begin()
{
    // init A part
    VFOsineA.begin(WAVEFORM_SINE);
    VFOsquareA.begin(WAVEFORM_SQUARE);
    VFOtriabgleA.begin(WAVEFORM_TRIANGLE);
    VFOsawA.begin(WAVEFORM_SAWTOOTH);
    VFOstringA.noteOff(0);
    VFOwhiteA.amplitude(0);
    VFOpinkA.amplitude(0);
    // init B part
    VFOsineB.begin(WAVEFORM_SINE);
    VFOsquareB.begin(WAVEFORM_SQUARE);
    VFOtriabgleB.begin(WAVEFORM_TRIANGLE);
    VFOsawB.begin(WAVEFORM_SAWTOOTH);
    VFOstringB.noteOff(0);
    VFOwhiteB.amplitude(0);
    VFOpinkB.amplitude(0);
    for (int i = 0; i < 8; i++)
    {
        waveMixA.gain(i, 0.125);
        waveMixB.gain(i, 0.125);
    }
    waveMixAB.gain(0, 0.5);
    waveMixAB.gain(1, 0.5);
    for (int i = 0; i < 4; i++)
    {
        VFOfilterMix.gain(i, 0.25);
    }
}

void noteOn(byte Note, byte velocity)
{
    note = Note;
    isNoteOn = 1;

    newAmp = (float)velocity*(1.0f / 127.0f);

    VFOsineA.frequency(GetBendedFreq(oscApitchMult));
    VFOsquareA.frequency(GetBendedFreq(oscBpitchMult));
    VFOtriangleA.frequency(GetBendedFreq(oscCpitchMult));
    VFOsawA.frequency(GetBendedFreq(oscDpitchMult));

    VFOsineA.amplitude(newAmp);
    VFOsquareA.amplitude(newAmp);
    VFOtriangleA.amplitude(newAmp);
    VFOsawA.amplitude(newAmp);

    VFOenvelope.noteOn();
}
void noteOff(byte velocity)
{
    isNoteOn = 0;
    if (!isSustain)
    {
        VFOenvelope.noteOff();
    }
}
bool isNotPlaying()
{
    if (!env.isActive())
        return true;
    else
        return false;
}
float GetBendedFreq(byte pitchMult)
{
    if (pitchMult < 64)
        return noteFreqs[note - 12*(64-pitchMult)];
    else if (pitchMult > 64)
        return noteFreqs[note + 12*(pitchMult-64)];
    else
        return noteFreqs[note];
}

void begin()
{
   mod.begin();
   for (int i = 0; i < NoteGenCount ; i++)
   {
       noteGen[i].begin();
       mixFinal.gain(i, 1.0/NoteGenCount );
   }
}

void noteOn(byte note, byte velocity)
{
    //digitalWrite(NOTE_PRESSED_STATE_LED, HIGH);
    for (int i = 0; i < NoteGenCount ; i++) 
    {
        if (noteGen[i].note == note) // first check if the note was played recently
        {
            noteGen[i].noteOn(note, velocity);
            //digitalWrite(NOTE_OVERFLOWN_LED, LOW);
            return; 
        }
    }
    for (int i = 0; i < NoteGenCount ; i++) // second see if there is any free "spot"
    {
        if (noteGen[i].isNotPlaying())
        {
            noteGen[i].noteOn(note, velocity);
            //digitalWrite(NOTE_OVERFLOWN_LED, LOW);
            return;
        }
    }
    //digitalWrite(NOTE_OVERFLOWN_LED, HIGH);
}
void noteOff(byte note, byte velocity)
{
    //digitalWrite(NOTE_PRESSED_STATE_LED, LOW);
    for (int i = 0; i < NoteGenCount ; i++)
    {
        if (noteGen[i].note == note)
        {
            noteGen[i].noteOff(velocity);
            return;
        }
    }
}
void activateSustain()
{
    for (int i = 0; i < NoteGenCount ; i++)
    {
        noteGen[i].isSustain = 1;
    }
}
void deactivateSustain()
{
    for (int i = 0; i < NoteGenCount ; i++)
    {
        noteGen[i].isSustain = 0;
        if (!noteGen[i].isNoteOn)
            noteGen[i].noteOff(0);
    }
}
