var niw = 150; // new item width
var nih = 150; // new item height
var nis = 0; // item spacing
var count = 11;
var newType = "UI_Piano";

// note that all positions x and y 
// are based from the middle of the rects

//RED.view.PianoNodeListTemp is used
// to store the prev. added items (sliders)
// so they can be removed before the script is run
// again (to avoid "duplicates")
// this list is NOT saved between instances of this "program"

if (d.nodes == undefined)
    d.nodes = [];
else // prev items exists
{
    // this clears previously added items
    for (var i = 0; i < d.nodes.length; i++)
    {
        removeNode(d.nodes[i]);
    }
    d.nodes = [];
}
redraw(true);
console.warn("script name:" + d.name +":"+ d.x + ":" + d.y + ":" + d.w + ":" +d.h);
for (var i = 0; i < count; i++)
{
    var newPiano = AddNewNode(0, 0, newType);
    newPiano.name = "octave" + i;
    newPiano.midiCh = 0;
    newPiano.octave = i;
    newPiano.sendCommand = '"midiSend("+(d.keyDown+d.midiCh)+","+(d.octave*12+d.keyIndex) +",127)"'
    newPiano.h = nih;
    newPiano.w = niw;
    if (posMode === 2)
    {
        newPiano.x = i*(niw+nis) + (d.x-d.w/2 + niw/2);
        newPiano.y = (d.y+d.h/2+nih/2);
    }
    else
    {
        newPiano.x = i*(niw+nis) + (d.x);
        newPiano.y = (d.y+d.h);
    }
    newPiano.blackKeysWidthDiff = 10;
    newPiano.dirty = true;

    newPiano.parentGroup = d;
    d.nodes.push(newPiano);

    //RED.view.PianoNodeListTemp.push(newPiano);

}
redraw(true);

